import React, { useState } from 'react';
import data from '../../helper/data';
import Employees from "./Employees"

function EmployeesList() {
    const [gap, setgap] = useState([0, 5]);
    const handleNext = () => setgap(gap.map((item) => item + 5));
    const handleReset = () => setgap([0, 5]);
    const handlePrev = () => setgap(gap.map((item) => item - 5));
    const handleLastPage = () => setgap([data.length - 5, data.length]);
    // console.log(data.length);
    return (
      <main>
        <section className="container">
          <h3>Employee List</h3>
          <h5>
            (Employees {gap[0] + 1} to {gap[1]})
          </h5>
          {data.slice(gap[0], gap[1]).map((person) => (
            <Employees {...person} />
          ))}
          <div className="btns">
            <button onClick={gap[1] <= 5 ? handleLastPage : handlePrev}>
              Prev
            </button>
            <button onClick={data.length <= gap[1] ? handleReset : handleNext}>
              Next
            </button>
          </div>
        </section>
      </main>
    );
  }

export default EmployeesList;
